import React, {Component} from "react";
import RenderTodoList from "./RenderTodoList.js"
import RenderTodoInput from "./RenderTodoInput.js";
class Todolist extends Component{
    constructor(props){
      super(props);
      this.contentsMaxIdx =0;
      this.state={
        contents :[]
      }
    }



    render(){
      console.log(this.contentsMaxIdx);
      console.log(this.state.contents.length);
      console.log(this.state.contents[this.contentsMaxIdx-1]);
      return(
        <div>
          {this.selecterControl(this.contentsMaxIdx-1)}
          <RenderTodoInput 
          contentsList = {this.state.contents} 
          selecterControl = {function(selectedIdx){
            console.log(selectedIdx);
          }}
          updateContents={function(updatelist){
            this.contentsMaxIdx = this.contentsMaxIdx+1;
            this.setState({
              contents : updatelist,
            })
          }.bind(this)}></RenderTodoInput>
          <RenderTodoList contentsData={this.state.contents}></RenderTodoList>
        </div>

      );
    }
  }

  export default Todolist;
