        //   case 1:
        //     swapSet = funcActionTM.swapTodoContents(todoSelector,listSelector, e.key, nowStage, prevTodoList);
        //     if(swapSet !=null) ActionTodoMemo(swapSet[0],swapSet[1],swapSet[2]);
        //   case 2:
        //     swapSet = funcActionTM.swapTodoContents(todoSelector,listSelector, e.key, nowStage, prevTodoList);
        //     if(swapSet !=null) ActionTodoMemo(swapSet[0],swapSet[1],swapSet[2]);
        // }
      }

      //move
      else if(37<=e.keyCode && e.keyCode<=40){
        moveSet = funcActionTM.moveTodoContents(todoSelector,listSelector, e.key, nowStage, prevTodoList)
        if(swapSet !=null) ActionTodoMemo(moveSet[0],moveSet[1],moveSet[2]);
        // //EnterTodo
        // if(nowStage === 0){

        // }
        // //FilterTodo
        // else if(nowStage === 1){

        // }
        // //EraseTodo
        // else if(nowStage === 2){

        // }
      }
      //Enter memo mode
      else if(e.key ==='/'){

      }
      //delete
      else if(e.shiftKey && e.key === 'Delete'){
        deleteSet = funcActionTM.deleteTodoContents(listSelector,todoSelector,prevTodoList);
        if(swapSet !=null) ActionTodoMemo(moveSet[0],moveSet[1],moveSet[2]);
      }
    })
  })





  return(
    //조건부 렌더링
    <div>
      {
      (nowStage === 0) ? (
        <RenderTodoMemoDivReady
          renderTodoList = {prevTodoList}
          todoSelector = {todoSelector}
          memoSelector = {memoSelector}
          todoMode = {todoMode}
        ></RenderTodoMemoDivReady>
      ) : null}



    </div>


    // <div>
    //   {props.nowStage === 0 ? (
    //     <ControllerTodoInput 
    //     _stage = {props.nowStage}
    //     _contents = {prevTodoList} 
    //     updateContents={nextTodoList}></ControllerTodoInput>
    //   ):null}
    //   {
    //     (props.nowStage === 0) ? (
    //       <ControllerReadyTodoList 
    //         _contents={prevTodoList}
    //         _stage = {props.nowStage}
    //         updateContentsTodoList={nextTodoList}
    //       ></ControllerReadyTodoList>
    //     ) : (
    //       (props.nowStage === 1) ? (
    //         <ControllerFilterTodoList 
    //           _contents={prevTodoList}
    //           _stage = {props.nowStage}
    //           updateContentsTodoList={nextTodoList}
    //       ></ControllerFilterTodoList>
    //       ) : (
    //         console.log("hi")
    //       )
    //     )


    //   }
    // </div>

  );

}
