import { render } from "@testing-library/react";
import React, {Component} from "react";
import "./TodoElement.css"




// window.addEventListener('keydown',(e)=>{
// if(e.shiftKey &&  37<=e.keyCode && e.keyCode<=40){
//     pressShiftCnt = pressShiftCnt+1;
//     pressArrowDirection = e.key;
//     console.log(pressShiftCnt , pressArrowDirection);
// }
// })





class RenderTodoList extends Component{
    constructor(props){
        super(props);
        this.state={
            pressShiftCnt :0,
            pressArrowDirection : '',
            selectPointerIdx : this.props._contents.length
        }
    }

    componentDidMount() {
        window.addEventListener('keydown',(e)=>{
            if(e.shiftKey &&  37<=e.keyCode && e.keyCode<=40){
                console.log(this.state.selectPointerIdx);

                var _pressShiftCnt = this.state.pressShiftCnt+1;
                var _pressArrowDirection = e.key;
                this.setState({
                    pressShiftCnt : _pressShiftCnt,
                    pressArrowDirection : _pressArrowDirection
                })
            }
        })
    }

    render(){
        var renderList = [];
        var renderContainer = this.props._contents;
        var i =renderContainer.length-1;
        if(this.props._mode === 'editTodo'){
            while(i>=0){
                if(i === renderContainer.length-1 && this.props._mode === 'editTodo'){
                    renderList.push(<div className="todoMainElement-now" key={renderContainer[i].todolist}>{renderContainer[i].todolist}</div>);
                }else{
                    renderList.push(<div className="todoMainElement" key={renderContainer[i].todolist}>{renderContainer[i].todolist}</div>);

                }
                i=i-1;
            }
        }else{
            while(i>=0){
                renderList.push(<div className="todoMainElement" key={renderContainer[i].todolist}>{renderContainer[i].todolist}</div>);
                i=i-1;
            }
        }
        return(
            <div>
                {renderList}
            </div>


        )
    }
}

export default RenderTodoList;
