import React, {Component} from "react";
import RenderTodoList from "./RenderTodoList.js"
import RenderTodoInput from "./RenderTodoInput.js";
class Todolist extends Component{
    constructor(props){
      super(props);
      this.selecterIdx =0;
      this.state={
        contents :[]
      }
    }

    selecterControl(){
      console.log(this.state.contents[0]);
    }


    render(){
      console.log(this.selecterIdx);
      console.log(this.state.contents.length);
      console.log(this.state.contents[this.selecterIdx-1]);
      return(
        <div>
          {this.selecterControl()}
          <RenderTodoInput 
          contentsList = {this.state.contents} 
          selecterIdx = {this.state.selector}
          updateContents={function(updatelist){
            this.selecterIdx = this.selecterIdx+1;
            this.setState({
              contents : updatelist,
            })
          }.bind(this)}></RenderTodoInput>
          <RenderTodoList contentsData={this.state.contents}></RenderTodoList>
        </div>

      );
    }
  }

  export default Todolist;
