// mode, contents
class RenderTodoList extends Component{
    constructor(props){
        super(props);
        this.state={
            pressShiftCnt :0,
            pressArrowDirection : '',
            currentSelector : 0,
            selectMode : false
        }
    }

    componentDidMount() {
        window.addEventListener('keydown',(e)=>{
            if(e.shiftKey &&  37<=e.keyCode && e.keyCode<=40){
                var _pressShiftCnt = this.state.pressShiftCnt+1;
                var _pressArrowDirection = e.key;
                if(this.state.currentSelector === 0){
                    this.setState({
                        pressShiftCnt : _pressShiftCnt,
                        pressArrowDirection : _pressArrowDirection,
                        currentSelector : this.props._contents.length-1
                    })
                }else{
                    this.setState({
                        pressShiftCnt : _pressShiftCnt,
                        pressArrowDirection : _pressArrowDirection,
                    })
                }
                console.log(this.state); console.log(this.props); console.log(this.state.currentSelector);
            }
        })
    }



    makeTodoElementByOrder(ArrowDirection, selectNumber, mode){
        var renderList =[];
        var renderContainer = this.props._contents;
        var i = renderContainer.length-1;
        if(ArrowDirection === 'ArrowUp') {
            if(this.selectPointer === this.props._contents.length-1){
                return renderContainer;
            }else{
                selectNumber = selectNumber+1;
            }

        }else if(ArrowDirection === 'ArrowDown'){
            if(this.selectPointer !== 0){
                selectNumber = selectNumber+1;
            }

        }
        if(mode === 'editTodo'){
            while(i>=0){
                if(i === selectNumber && this.props._mode === 'editTodo'){
                    renderList.push(<div className="todoMainElement-now" key={renderContainer[i].todolist}>{renderContainer[i].todolist}</div>);
                }else{
                    renderList.push(<div className="todoMainElement" key={renderContainer[i].todolist}>{renderContainer[i].todolist}</div>);

                }
                i=i-1;
            }
        }else{
            //render default -> input todolist
            while(i>=0){
                renderList.push(<div className="todoMainElement" key={renderContainer[i].todolist}>{renderContainer[i].todolist}</div>);
                i=i-1;
            }
        }
        return renderList;
    }


    render(){
        var renderList = this.makeTodoElementByOrder('None', this.props._contents.length, 'editTodo');
        // var renderList = [];
        // var renderContainer = this.props._contents;
        // var i =renderContainer.length-1;
        // if(this.props._mode === 'editTodo'){
        //     while(i>=0){
        //         if(i === renderContainer.length-1 && this.props._mode === 'editTodo'){
        //             renderList.push(<div className="todoMainElement-now" key={renderContainer[i].todolist}>{renderContainer[i].todolist}</div>);
        //         }else{
        //             renderList.push(<div className="todoMainElement" key={renderContainer[i].todolist}>{renderContainer[i].todolist}</div>);

        //         }
        //         i=i-1;
        //     }
        // }else{
        //     while(i>=0){
        //         renderList.push(<div className="todoMainElement" key={renderContainer[i].todolist}>{renderContainer[i].todolist}</div>);
        //         i=i-1;
        //     }
        // }
        return(
            <div>
                {renderList}
            </div>


        )
    }
}

export default RenderTodoList;
