import React, {Component} from "react";
import RenderTodoList from "./RenderTodoList.js"
import RenderTodoInput from "./RenderTodoInput.js";
import Modal from "./Modal.js";



class Todolist extends Component{
    constructor(props){
      super(props);
      // stage Section : EnterTodo, FilterTodo, EraseTodo
      this.stage="EnterTodo";
      // this.contentsMaxIdx =0;
      this.state={
        contents :[],
        stage : "EnterTodo",
        nextbooleaon :false
      }
    }
    componentDidMount(){
      window.addEventListener('keydown',(e)=>{
        if(e.shiftKey && e.key ==='Enter'){
          this.setState({
            stage : "FilterTodo",
            nextbooleaon : !this.state.nextbooleaon

          })
        }
      })
    }

    modalMessage(){
      if(this.stage==="EnterTodo"){
        return "Do you want to go filtering step?"
      } 
    }


    render(){
      // this.nextStange()
      this.modalMessage();
      return(
        <div>
          <Modal
          _stage={this.state.stage}
          _nextbooleaon={this.state.nextbooleaon}
          _header="Modal heading"
          >
            {modalMessage()}
          </Modal>


          <RenderTodoInput 
          _stage = {this.stage}
          _contents = {this.state.contents} 

          updateContents={function(updatelist){
            this.setState({
              contents : updatelist,
            })
          }.bind(this)}></RenderTodoInput>
          <RenderTodoList 
            _contents={this.state.contents}
            _stage = {this.stage}

            updateContentsTodoList={function(updatelist){
              this.setState({
                contents : updatelist
              })
            }.bind(this)}
          ></RenderTodoList>
        </div>

      );
    }
  }

  export default Todolist;
