import React, {Component} from "react";
import RenderTodoList from "./TodoList/RenderTodoList.js"
class Todolist extends Component{
    constructor(props){
      super(props);
      this.state={
        contents:[]
      }
    }




    render(){
      return(
        <div>
          <RenderTodoList dataTodoList={this.props.state.contents}></RenderTodoList>
          <input 
          type="text"
          placeholder='add your anxity'
          onKeyPress={function(e){
              if(e.nativeEvent.key === 'Enter' && e.nativeEvent.shiftKey){
                console.log(this.state.contents);
                var addContents = Array.from(this.state.contents);
                var addTodoElement = {state : true, todolist : e.target.value}
                addContents.push(addTodoElement);
                this.setState({
                  contents : addContents
                });
              }
            }.bind(this)
          }
          ></input>
        </div>
      );
    }
  }

  export default Todolist;
