import React, {Component} from "react";
import RenderTodoList from "./RenderTodoList.js"
import RenderTodoInput from "./RenderTodoInput.js";
import Modal from "./Modal.js";



class Todolist extends Component{
    constructor(props){
      super(props);
      // stage Section : EnterTodo, FilterTodo, EraseTodo
      // this.contentsMaxIdx =0;
      this.stageState = ["EnterTodo","FilterTodo","EraseTodo"]
      this.state={
        contents :[],
        stage : "EnterTodo",
        nextbooleaon :false,
        modalState : false,
        wheretogo : true
      }
    }
    componentDidMount(){
      window.addEventListener('keydown',(e)=>{



        if(e.shiftKey && e.key ==='Enter' && this.state.modalState){
          this.setState({
            stage : "FilterTodo",
            modalState : !this.state.modalState
          })
        }
        else if(e.shiftKey && e.key === 'Delete' && this.state.modalState){
          this.setState({
            stage : "EnterTodo",
            modalState : !this.state.modalState
          })
        }
        else if(e.shiftKey && (e.key==='Enter' || e.key === 'Delete') && !this.state.modalState){
          if(e.key==='Enter'){
            this.setState({
              modalState : !this.state.modalState
            })
          }else{
            this.setState({
              modalState : !this.state.modalState,
              wheretogo : !this.state.wheretogo
            })
          }

        }

      })
    }

    modalMessage(){
      let returnstr;
      if(this.state.stage==="EnterTodo"){
        returnstr = "Are you sure want to go 'Next' step?";
      }else if(this.state.stage === "FilterTodo"){
        returnstr = "Are you sure want to go 'Previous' step?"
      }
      return returnstr;
    }


    render(){
      console.log(this.state.stage);
      return(
        <div>
          <Modal
          _modalState={this.state.modalState}
          _header={this.state.stage}
          _wheretogo={this.state.wheretogo}
          >
            {this.modalMessage()}
          </Modal>


          <RenderTodoInput 
          _stage = {this.state.stage}
          _contents = {this.state.contents} 

          updateContents={function(updatelist){
            this.setState({
              contents : updatelist,
            })
          }.bind(this)}></RenderTodoInput>
          <RenderTodoList 
            _contents={this.state.contents}
            _stage = {this.state.stage}

            updateContentsTodoList={function(updatelist){
              this.setState({
                contents : updatelist
              })
            }.bind(this)}
          ></RenderTodoList>
        </div>

      );
    }
  }

  export default Todolist;
