import React, {Component} from "react";
import RenderTodoList from "./RenderTodoList.js"
import RenderTodoInput from "./RenderTodoInput.js";



class Todolist extends Component{
    constructor(props){
      super(props);
      // stage Section : EnterTodo, FiterTodo, EraseTodo
      this.stage="EnterTodo";
      // this.contentsMaxIdx =0;
      this.state={
        contents :[],
      }
    }

    // nextStange (){
    //   window.addEventListener('keydown',(e)=>{
    //     if(e.shiftKey && e.key ==='Enter'){
    //       console.log(e);
    //     }
    //   })
    // }


    componentDidMount(){
      window.addEventListener('keydown',(e)=>{
        if(e.shiftKey && e.key ==='Enter'){
          console.log("Todolist Compoenet");
        }
      })
    }


    render(){
      // this.nextStange()
      return(
        <div>
          <RenderTodoInput 
          _stage = {this.stage}
          _contents = {this.state.contents} 
          updateContents={function(updatelist){
            // this.contentsMaxIdx = this.contentsMaxIdx+1;
            this.setState({
              contents : updatelist,
            })
          }.bind(this)}></RenderTodoInput>
          <RenderTodoList 
            _contents={this.state.contents}
            _stage = {this.stage}

            updateContentsTodoList={function(updatelist){
              this.setState({
                contents : updatelist
              })
            }.bind(this)}
          ></RenderTodoList>
        </div>

      );
    }
  }

  export default Todolist;
