      // 모달이 열릴때 openModal 클래스가 생성된다.
      <div className={ _nextbooleaon ? 'openModal modal' : 'modal'}>
        {_nextbooleaon ? (
          <section>
            <header>
              {_header}
              <button className="close">
                &times;
              </button>
            </header>
            <main>{props.children}</main>
            <footer>
              <button className="close">
                shitf + Delete 
              </button>
              <button className="go_next">
                  shift + Enter
              </button>
            </footer>
          </section>
        ) : null}
      </div>
    );
  };



