import React, {Component} from "react";

class RenderTodoInput extends Component{
    constructor(propst){
        super(props);
        this.state={
            renderContainer : this.inputData
        }
    }

    updateContentChildFunc(updatelist){
        this.props.updateContents(updatelist);
    }

    render(){
        return(
            <input 
                type="text"
                placeholder='add your anxity'
                onKeyPress={function(e){
                    if(e.nativeEvent.key === 'Enter' && e.nativeEvent.shiftKey){
                        e.preventDefault();
                        var addContents = Array.from(renderContainer);
                        var addTodoElement = {state : true, todolist : e.target.value}
                        addContents.push(addTodoElement);
                        updateContentChildFunc(addContents);
                        // this.setState({
                        //     renderContainer : addContents
                        // });
                        e.target.value = "";
                    }
                }
            }
          ></input>
        )
    }
}

export default RenderTodoInput;
