import React, {Component} from "react";

class RenderTodoInput extends Component{
    constructor(props){
        super(props);
        this.state={
            inputData : this.props.inputData,
            updateContent : this.props.updateContents
        }
    }



    render(){
        return(
            <input 
                type="text"
                placeholder='add your anxity'
                onKeyPress={function(e){
                    if(e.nativeEvent.key === 'Enter' && e.nativeEvent.shiftKey){
                        e.preventDefault();
                        var addContents = Array.from(this.state.inputData);
                        var addTodoElement = {state : true, todolist : e.target.value}
                        addContents.push(addTodoElement);
                        this.state.updateContent(e,addContents);
                        e.target.value = "";
                    }
                }.bind(this)
            }
          ></input>
        )
    }
}

export default RenderTodoInput;
