import React, {Component} from "react";
import RenderTodoList from "./RenderTodoList.js"
import RenderTodoInput from "./RenderTodoInput.js";



class Todolist extends Component{
    constructor(props){
      super(props);
      this.contentsMaxIdx =0;
      this.state={
        contents :[],
        _todoMode : 'writeTodo',
        _currentSelector : 0,
        _pressCnt : 0
      }
    }





    render(){
      console.log(this.props._pressShiftCnt , this.props._pressArrowDirection)
      return(
        <div>
          <RenderTodoInput 
          _contents = {this.state.contents} 
          _mode = {this.state._todoMode}
          modeChange = {function(changeMode){
            this.setState({
              _todoMode : changeMode
            })
            console.log(this.state._currentSelector);
          }.bind(this)}
          updateContents={function(updatelist){
            this.contentsMaxIdx = this.contentsMaxIdx+1;
            this.setState({
              contents : updatelist,
              _currentSelector : this.contentsMaxIdx
            })
          }.bind(this)}></RenderTodoInput>
          <RenderTodoList 
            _mode = {this.state._todoMode}
            _contents={this.state.contents}
            _event = {window.addEventListener('keydown',(e) =>{})}
          ></RenderTodoList>
        </div>

      );
    }
  }

  export default Todolist;
