import React, {Component, useState} from 'react';
import './App.css';
import ClockCheckNow from './TodoList/ClockCheckNow.js';
import Todolist from './TodoList/Todolist.js'
import Welcome from './TodoList/Welcome.js'

class History_nav extends Component{
  render(){
    return(
      <nav>
        <div>LOGIN</div>
        <div>CALENDER</div>
        <div>STATICS</div>
      </nav>
    );
  }
}








function App() {

  const [pressShift, _pressShift] = useState(false);
  const [pressArrow, _pressArrow] = useState('');

  const changeShiftMode = (e) =>{
    // console.log(e.shiftKey);
    _pressShift(true);
  }
  const changeArrowMode = (e) =>{
    // console.log(e.key);
    _pressArrow(e.key);
  }

  React.useEffect(() => {

    window.addEventListener('keydown', (e) => {
      if(e.shiftKey &&  37<=e.keyCode && e.keyCode<=40){
        changeArrowMode(e); changeShiftMode(e);
      }
    });

  });

  // console.log({pressArrow}, {pressShift});
  // React.useEffect(() => {

  //   window.addEventListener('keydown', (e) => {
  //     pressShift = false;
  //     pressArrow ='';
