  //     if(e.shiftKey &&  37<=e.keyCode && e.keyCode<=40){
  //       // changeShift(); changeShift();  
  //     }
  //     // console.log(pressShift + pressArrow);
  //   });

  // });

  return (
    <div className="App">
      <header className="App-header">

        <History_nav></History_nav>
        <Welcome ></Welcome>
        <ClockCheckNow></ClockCheckNow>
        <Todolist changeShiftMode={pressShift} changeArrowMode ={pressArrow} ></Todolist>
      </header>
    </div>
  );
}

export default App;
