import React, {Component} from "react";
import RenderTodoList from "./RenderTodoList.js"
import RenderTodoInput from "./RenderTodoInput.js";
class Todolist extends Component{
    constructor(props){
      super(props);
      this.state={
        contents:[],
        selector : 0
      }
    }

    changeMode(){
      this.setState({
        selector :1
      })
      if(this.state.selector === 1){
        var selectContents = Array.from(this.state.contents);
        selectContents.push({state: true, todolist : "this is temp"});
        this.setState({
          contents : selectContents
        })
      }
    }



    render(){
      return(
        <div>
          <RenderTodoList contentsData={this.state.contents}></RenderTodoList>
          <RenderTodoInput contentsList = {this.state.contents} updateContents={function(updatelist){
            this.setState({
              contents : updatelist
            })
          }.bind(this)}></RenderTodoInput>

          {/* <input 
          type="text"
          placeholder='add your anxity'
          onKeyPress={function(e){
              if(e.nativeEvent.key === 'Enter' && e.nativeEvent.shiftKey){
                e.preventDefault();
