                addContents.push(addTodoElement);
                this.setState({
                  contents : addContents
                });
                e.target.value = "";
              }
            }.bind(this)
          }
          ></input> */}
          {this.changeMode()}
        </div>
      );
    }
  }

  export default Todolist;
