import React, {Component, useState} from 'react';
import './App.css';
import ClockCheckNow from './TodoList/ClockCheckNow.js';
import Todolist from './TodoList/Todolist.js'
import Welcome from './TodoList/Welcome.js'

class History_nav extends Component{
  render(){
    return(
      <nav>
        <div>LOGIN</div>
        <div>CALENDER</div>
        <div>STATICS</div>
      </nav>
    );
  }
}








function App() {

  const [pressShift, _pressShift] = useState(false);
  const [pressArrow, _pressArrow] = useState('');
  const [pressTime, _pressTime] = useState(0);
  const changeShiftMode = (e) =>{
    _pressShift(true);
  }
  const changeArrowMode = (e) =>{
    _pressArrow(e.key);
  }

  React.useEffect(() => {
    window.addEventListener('keyup', (e) => {
      if(e.shiftKey &&  37<=e.keyCode && e.keyCode<=40){
        _pressArrow(e.key); changeShiftMode(e); _pressTime(e.timeStamp);
      }
    });

  }, [pressTime]);

  return (
    <div className="App">
      <header className="App-header">

        <History_nav></History_nav>
        <Welcome ></Welcome>
        <ClockCheckNow></ClockCheckNow>
        <Todolist changeShiftMode={pressShift} changeArrowMode ={pressArrow} ></Todolist>
      </header>
    </div>
  );
}

export default App;
