import React, {Component} from "react";
import RenderTodoList from "./RenderTodoList.js"
import RenderTodoInput from "./RenderTodoInput.js";
import Modal from "./Modal.js";



class Todolist extends Component{
    constructor(props){
      super(props);
      // stage Section : EnterTodo, FilterTodo, EraseTodo
      // this.contentsMaxIdx =0;
      this.stageState = ["EnterTodo","FilterTodo","EraseTodo"]
      this.state={
        contents :[],
        stage : "EnterTodo",
        modalState : false
      }
    }

    modalMessage(NextPrev){
      let returnstr;
      if(NextPrev==='Next'){
        returnstr = "1s after you go to Next step";
      }else if(NextPrev==='Prev'){
        returnstr = "1s after you go to Previous step"
      }
      return returnstr;
    }



    componentDidMount(){
      window.addEventListener('keydown',(e)=>{
        // console.log(e);


        if(e.shiftKey && e.key ==='Enter'){
          this.setState({
            stage : "FilterTodo",
            modalState : !this.state.modalState
          })
        }
        else if(e.shiftKey && e.key === 'Backspace'){
          this.setState({
            stage : "EnterTodo",
            modalState : !this.state.modalState
          })
        }


      })
    }




    render(){
      console.log(this.state.stage);
      return(
        <div>
          <Modal
          _modalState={this.state.modalState}
          _header={this.state.stage}

          closeModal={function(closeState){
            this.setState({
              modalState : false
            })
          }.bind(this)}
          >
            {this.modalMessage()}
          </Modal>


          <RenderTodoInput 
          _stage = {this.state.stage}
          _contents = {this.state.contents} 

          updateContents={function(updatelist){
            this.setState({
              contents : updatelist,
            })
          }.bind(this)}></RenderTodoInput>
          <RenderTodoList 
            _contents={this.state.contents}
            _stage = {this.state.stage}

            updateContentsTodoList={function(updatelist){
              this.setState({
                contents : updatelist
              })
            }.bind(this)}
          ></RenderTodoList>
        </div>

      );
    }
  }

  export default Todolist;
