import React, {Component} from "react";

class RenderTodoInput extends Component{
    render(){
        return(
            <input 
                type="text"
                placeholder='add your anxity'
                onKeyPress={function(e){
                    if(e.nativeEvent.key === 'Enter' && e.nativeEvent.shiftKey && this.props._mode === 'writeTodo'){
                        e.preventDefault();
                        var addContents = Array.from(this.props._contents);
                        var addTodoElement = {eraseState : false,selectPointer : false, todolist : e.target.value}
                        addContents.push(addTodoElement);
                        this.props.updateContents(addContents);
                        e.target.value = "";
                    }
                }.bind(this)
            }
          ></input>
        )
    }
}

export default RenderTodoInput;
