import React ,{useState}from "react";
import MemoInput from "./MemoInput";
import "./TodoElement.css"

function TodoMemoDiv({_contents,_mode,_currentSelector,_writeContentMode}){
    const [preContents, AfterContents] = useState(_contents);
    var returnList =[]

    const setAfterContents =(update)=>{
        AfterContents(update);
    }

    const makeMemoDivList =(memoListByContent)=>{
        console.log(memoListByContent);
        return(
            memoListByContent.map((memoElement)=>(
                <div className="memoElement" key={i+memoElement}>{memoElement}</div>
            ))
        )
    }

    const makeTodoDivList =(_className, _renderContainerElement,_inputMemo)=>{
        if(_inputMemo){
            return(
                <div className={_className} key={_renderContainerElement.todolist}>
                    {renderContainer[i].todolist}

                    <div className="memoInput" key={_renderContainerElement.todolist +"mempInputs"}>
                        <MemoInput
                            _memoContents = {renderContainer}
                            _memoIdx = {_currentSelector}
                            _setterContents = {setAfterContents}
                        ></MemoInput> 
                    </div>
                    {makeMemoDivList(_renderContainerElement.memolist)}
                </div>
            )
        }
    }


    var renderContainer = Array.from(_contents);
    var i=0;

    if(_mode === 'selectorMode'){
        while(i<renderContainer.length){
            if(_currentSelector!==-1 && i===_currentSelector){
                if(_writeContentMode ==='memoList'){
                    returnList.push(

                        <div className="todoMainElement-now" key={renderContainer[i].todolist}>
                            <div>
                            {makeTodoDivList("todoMainElement-now",renderContainer[i],true)}
                            </div>
                            {renderContainer[i].todolist}

                            <div className="memoInput" key={renderContainer[i].todolist +"mempInputs"}>
                                <MemoInput
                                    _memoContents = {renderContainer}
                                    _memoIdx = {_currentSelector}
                                    _setterContents = {setAfterContents}
                                ></MemoInput> 
                            </div>
                            {makeMemoDivList(renderContainer[i].memolist)}
                        </div>
                    );
                }else{
                    returnList.push(
                        <div className="todoMainElement-now" key={renderContainer[i].todolist}>
                            {renderContainer[i].todolist}
                            {makeMemoDivList(renderContainer[i].memolist)}
                        </div>
                    );
                }
            }else{
                returnList.push(
                    <div className="todoMainElement" key={renderContainer[i].todolist}>
                        {renderContainer[i].todolist}
                        {makeMemoDivList(renderContainer[i].memolist)}
                    </div>
                );
            }
            i=i+1;
        }//end while
    }else{
        while(i<renderContainer.length){
            returnList.push(
            <div className="todoMainElement" key={renderContainer[i].todolist}>
                {renderContainer[i].todolist}
                {makeMemoDivList(renderContainer[i].memolist)}
            </div>
            );

            i=i+1
        }
    }
    return(
        returnList
    );
};

export default TodoMemoDiv;
